select * from ( select (@ROWNUM := @ROWNUM + 1) rn, c.* from ( select H.h_id, H.h_name, H.h_phone, H.h_email1, H.h_email2, H.deputy_name, H.deputy_phone, H.deputy_birth, H.deputy_department, I.hostInfo_name, I.h_code, R.room_code, I.h_sellerNum, I.h_accountNum, I.zipcode, I.roadAddress, I.numberAddress, I.restAddress, I.host_type, R.room_type, R.room_number, R.room_fee, R.able_checkIn, R.able_checkOut, R.title, R.content, R.room_status, R.min_number, R.max_number, R.bed_type, R.bed_count, R.bath_count, R.party_check, R.kitchen, R.wifi, R.parking, R.tv, R.refrigerator, R.hairdryer, R.washer, R.pet, R.pickup, R.dryer, R.elevator, R.barbecue, R.pool, R.aircon, R.heater, R.smoking, R.creDate, R.del_yn from h_host H inner join h_host_info I on H.h_id = I.h_id inner join h_room R on I.h_code = R.h_code where R.room_code > 0
and H.h_id like concat('%',#{h_id2},'%')
and R.creDate >= #{join_startDate}
and R.creDate <= #{join_endDate}
and H.h_name like concat('%',#{h_name2},'%')
and R.del_yn = #{r_del_yn}
and I.h_sellerNum like concat('%',#{h_sellerNum2},'%')
and I.hostInfo_name like concat('%',#{hostInfo_name2},'%')
and I.roadAddress like concat('%',#{roadAddress2},'%')
order by R.room_code desc )c,(select @ROWNUM := 0)s)b where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}

   		
