select * from ( select (@ROWNUM := @ROWNUM + 1) rn, c.* from ( select O.order_code, M.u_id, I.h_code, R.room_code, O.order_name, O.order_phone, O.order_email1, O.order_email2, O.checkIn_date, O.checkOut_date, O.people_count, O.room_fee, O.pay_type, O.discount, O.total, O.use_state, O.pay_state, O.creDate, O.review_state, O.reservation_state, M.u_point, M.u_grade, I.hostInfo_name, I.zipcode, I.roadAddress, I.numberAddress, I.restAddress, I.host_type, H.h_name, H.h_phone, H.h_email1, H.h_email2, H.deputy_name, H.deputy_phone, R.room_number from u_order O inner join u_member M on O.u_id = M.u_id inner join h_room R on O.room_code = R.room_code inner join h_host_Info I on O.h_code = I.h_code inner join h_host H on I.h_id = H.h_id where O.order_code > 0
and M.u_id like concat('%',#{u_id2},'%')
and O.creDate >= #{join_startDate}
and O.creDate <= #{join_endDate}
and O.order_name like concat('%',#{u_name2},'%')
and O.pay_state = #{pay_state}
and O.order_code like concat('%',#{order_code2},'%')
order by creDate desc )c,(select @ROWNUM := 0)s)b where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}

select * from ( select (@ROWNUM := @ROWNUM + 1) rn, c.* from ( select O.order_code, M.u_id, I.h_code, R.room_code, R.room_type, O.order_name, O.order_phone, O.order_email1, O.order_email2, O.checkIn_date, O.checkOut_date, O.people_count, O.room_fee, O.pay_type, O.discount, O.total, O.use_state, O.pay_state, O.creDate, O.review_state, O.reservation_state, M.u_point, M.u_grade, I.hostInfo_name, I.zipcode, I.roadAddress, I.numberAddress, I.restAddress, I.host_type, H.h_name, H.h_phone, H.h_email1, H.h_email2, H.deputy_name, H.deputy_phone, R.room_number from u_order O inner join u_member M on O.u_id = M.u_id inner join h_room R on O.room_code = R.room_code inner join h_host_Info I on O.h_code = I.h_code inner join h_host H on I.h_id = H.h_id where O.order_code > 0
and M.u_id = #{u_id}
order by creDate desc )c,(select @ROWNUM := 0)s)b limit 10

		SELECT count(*) FROM u_order where people_count > 0
    		
    	 
